
(function($){$.fn.neatShow=function(option){var selection=this;var settings=$.extend({minSpeed:400,maxSpeed:1800,speed:400,mode:null},option);if(settings.mode==='container'){selection=this.find('img:not(:visible)');}
var neat=function(){var speed=settings.speed;if(speed==='random'){speed=Math.floor(Math.random()*(settings.maxSpeed-settings.minSpeed+1)+settings.minSpeed);}
$(this).fadeIn(speed);};selection.each(function(){if(this.complete||this.readyState==='complete'){neat.call(this);}else{$(this).load(neat);}});return this;};}(jQuery));